import { use } from 'react';
import Logo from './Logo';
import { usePathname } from "next/navigation";

function NavLinks({}) {
    const pathname = usePathname() || "";

    const navLinks = [
        { href: "#about", label: "About" },
        { href: "#experience", label: "Experience" },
        { href: "#projects", label: "Projects" },
        { href: "#contact", label: "Contact" },
    ];

    return (
      <div>
        {/* Desktop Navigation */}
        <ul className="flex gap-8 list-none text-2xl">
          {navLinks.map((link) => (
            <a
              key={link.href}
              href={link.href}
              className={`relative font-medium py-2 text-gray-800 hover:text-black after:absolute after:bottom-0 after:left-1/2 after:-translate-x-1/2 after:h-[2px] after:w-0
              after:bg-gray-800 after:transition-all after:duration-500
              hover:after:w-full ${
                pathname === link.href
                  ? "after:w-full"
                  : "after:w-0 hover:after:w-full"
              }`}
            >
              <span>{link.label}</span>
            </a>
          ))}
        </ul>
        {/* Mobile Navigation */}
      </div>
    );
}

export default NavLinks;
