import React, { useState, useEffect, useRef } from 'react';

// The ProjectCard now accepts a 'delay' prop for staggering the animation.
function ProjectCard({ title, image, features, githubUrl, delay = 0 }) {
    const [isVisible, setIsVisible] = useState(false);
    const cardRef = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(
            ([entry]) => {
                // If the element intersects the viewport, set isVisible to true.
                if (entry.isIntersecting) {
                    setIsVisible(true);
                    // Stop observing once it's visible to prevent re-triggering.
                    observer.unobserve(entry.target);
                }
            },
            {
                // Root margin controls when the observer fires. 
                // Firing 50px before the element reaches the bottom of the screen.
                rootMargin: '0px 0px -50px 0px', 
                threshold: 0.1, // 10% of the element must be visible
            }
        );

        if (cardRef.current) {
            observer.observe(cardRef.current);
        }

        // Cleanup function
        return () => {
            if (cardRef.current) {
                // Clean up the observer when the component unmounts
                observer.unobserve(cardRef.current);
            }
        };
    }, []); // Empty dependency array ensures this runs only once on mount

    // Conditional classes for the scroll animation:
    // 1. Initial State (Hidden): opacity-0 translate-y-8
    // 2. Transition: transition-all duration-700 ease-out 
    // 3. Visible State: opacity-100 translate-y-0 (applied when isVisible is true)
    const animationClasses = isVisible
        ? 'opacity-100 translate-y-0'
        : 'opacity-0 translate-y-8';

    // The delay property is applied via inline style for the staggering effect.
    const delayStyle = {
        transitionDelay: `${delay}ms`
    };

    return (
      <div 
        ref={cardRef} // Attach the ref to the element we want to observe
        // Use group for hover effects, and apply animation classes and delay style
        className={`group md:w-1/3 p-6 rounded-3xl border border-gray-600 border-solid text-center relative ${animationClasses}`}
        style={delayStyle}
      >
        <div className="w-full h-64 overflow-hidden rounded-[2rem] mb-4 relative">
          <img
            src={image}
            alt="Project"
            className="h-full w-full object-cover transition-transform duration-500 ease-in-out group-hover:scale-105"
          />

          {/* Overlay Content */}
          <div
            className="absolute inset-0 bg-black/80 flex flex-col items-start justify-center p-6 text-white opacity-0 transition-opacity duration-500 ease-out group-hover:opacity-100"
          >
            <h3 className="text-2xl font-semibold mb-3">{title}</h3>
            <ul className="grid gap-2 text-sm text-white/90 list-none p-0 m-0">
              {features.map((feature, index) => (
                <li key={index} className="flex items-start gap-2">
                  {/* Custom bullet point */}
                  <span
                    className="mt-1 h-1.5 w-1.5 rounded-full bg-white/80 flex-shrink-0"
                    aria-hidden="true"
                  />
                  {feature}
                </li>
              ))}
            </ul>
          </div>

        </div>
        <h2 className="font-[600] text-[1.75rem] mb-[2rem] m-[1rem]">
          {title}
        </h2>
        <div className="flex justify-center gap-[1rem]">
          <button
            className="font-[600] transition-all duration-300 ease-in p-[1rem] w-[9rem] rounded-[2rem] border border-solid border-[#ffffff] bg-white text-black hover:cursor-pointer hover:bg-black hover:text-white"
            onClick={() => {
              const url = githubUrl || "https://github.com/jrsussner18";
              const newWindow = window.open(
                url,
                "_blank",
                "noopener,noreferrer"
              );
              if (newWindow) newWindow.opener = null;
            }}
          >
            Github
          </button>
        </div>
      </div>
    );
}

export default ProjectCard;
